let countryList = [
  "math",
  "homework",
  "romance music and movies",
  "homophobics", 
  "ducks",
  "beans",
  "creepers",
  "squirrels",
  "school lunch food",
  "short nails"
];

let correctCountries= [];

countryList.forEach(function(country){
  let container =document.querySelector(".countryContainer");
  let newEl = document.createElement("p");
  newEl.id = country.replaceAll(" ", "");
  newEl.innerHTML = country;
  newEl.style.visibility = "hidden";
  container.append(newEl);
})

function submitAnswer(that){
  let answer = that.value;
  that.value = "";

  if (countryList.includes(answer) && !correctCountries.includes(answer) ){
    showAnswer(answer);
    correctCountries.push(answer)
  }

  if(correctCountries.length === countryList.length){
    document.getElementById("directions").innerHTML= "You win!!!!!!";
  }
}
function showAnswer(answer){
  let countryParagraph = document.querySelector("#" + answer.replaceAll(" ", ""))
  countryParagraph.style.visibility = "visible"
}
